import React, { useEffect, useRef, useState } from "react";
import "./ImageSlider.css";

const images = [
  {
    url: "https://images.unsplash.com/photo-1582407947304-fd86f028f716?q=80&w=1296&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#luxury-apartments",
    alt: "Luxury Apartment"
  },
  {
    url: "https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#modern-cafe",
    alt: "Modern Cafe"
  },
  {
    url: "https://images.unsplash.com/photo-1560185127-6ed189bf02f4?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#cozy-living",
    alt: "Cozy Living"
  },
  {
    url: "https://images.unsplash.com/photo-1560184897-ae75f418493e?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#urban-style",
    alt: "Urban Style"
  },
  {
    url: "https://images.unsplash.com/photo-1505873242700-f289a29e1e0f?q=80&w=1176&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#premium-office",
    alt: "Premium Office"
  },
  {
    url: "https://images.unsplash.com/photo-1507089947368-19c1da9775ae?q=80&w=1176&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#dining-space",
    alt: "Dining Space"
  }
];

const ImageSlider = () => {
  const [current, setCurrent] = useState(0);
  const timeoutRef = useRef(null);
  const delay = 3500;

  useEffect(() => {
    timeoutRef.current && clearTimeout(timeoutRef.current);
    timeoutRef.current = setTimeout(
      () => setCurrent((prev) => (prev + 1) % images.length),
      delay
    );
    return () => clearTimeout(timeoutRef.current);
  }, [current]);

  const goTo = (idx) => setCurrent(idx);
  const prev = () => setCurrent((prev) => (prev - 1 + images.length) % images.length);
  const next = () => setCurrent((prev) => (prev + 1) % images.length);

  return (
    <div className="image-slider-container">
      <button className="slider-arrow left" onClick={prev} aria-label="Previous image">&#8592;</button>
      <div className="slider-image-wrapper">
        <a href={images[current].link} tabIndex={0}>
          <img
            src={images[current].url}
            alt={images[current].alt}
            loading="lazy"
            className="slider-image"
          />
        </a>
      </div>
      <button className="slider-arrow right" onClick={next} aria-label="Next image">&#8594;</button>
      <div className="slider-dots">
        {images.map((img, idx) => (
          <button
            key={img.url}
            className={`slider-dot${idx === current ? " active" : ""}`}
            onClick={() => goTo(idx)}
            aria-label={`Go to slide ${idx + 1}`}
          />
        ))}
      </div>
    </div>
  );
};

export default ImageSlider;
