import React, { useEffect } from 'react';
import './Modal.css';

const Modal = ({ isOpen, onClose, area, onViewListings }) => {
  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
      document.body.style.overflow = 'hidden';
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
    };
  }, [isOpen, onClose]);

  if (!isOpen || !area) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleViewListings = () => {
    onClose();
    if (onViewListings && area.areaKey) {
      onViewListings(area.areaKey);
    }
  };

  return (
    <div className="modal-overlay" onClick={handleBackdropClick}>
      <div className="modal-content">
        <button className="modal-close" onClick={onClose}>
          <i className="fas fa-times"></i>
        </button>
        
        <div className="modal-left">
          <div className="modal-map">
            <img src="/assets/map.webp" alt="Area Map" />
          </div>
        </div>

        <div className="modal-right">
          <h2>{area.selectedSubArea ? area.selectedSubArea.title : area.name}</h2>
          <div className="modal-description">
            <h3>{area.name}</h3>
            <p>{area.description}</p>
            {area.selectedSubArea && (
              <div className="selected-subarea">
                <h4>About {area.selectedSubArea.title}</h4>
                <p>{area.selectedSubArea.description}</p>
              </div>
            )}
          </div>
          <div className="modal-button">
            <button className="btn btn-primary" onClick={handleViewListings}>
              View All Listings in {area.name}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
