import React, { useState, useEffect } from 'react';
import './PropertyDetailModal.css';

const PropertyDetailModal = ({ property, isOpen, onClose }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
      document.body.classList.add('modal-open');
    } else {
      document.body.style.overflow = 'unset';
      document.body.classList.remove('modal-open');
    }

    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
      document.body.classList.remove('modal-open');
    };
  }, [isOpen, onClose]);

  if (!isOpen || !property) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const nextImage = () => {
    if (property.images && property.images.length > 0) {
      setCurrentImageIndex((prev) => (prev + 1) % property.images.length);
    }
  };

  const prevImage = () => {
    if (property.images && property.images.length > 0) {
      setCurrentImageIndex((prev) => (prev - 1 + property.images.length) % property.images.length);
    }
  };

  const goToImage = (index) => {
    setCurrentImageIndex(index);
  };

  return (
    <div className="property-modal-overlay" onClick={handleBackdropClick}>
      <div className="property-modal-content">
        <button className="property-modal-close" onClick={onClose}>
          <i className="fas fa-times"></i>
        </button>

        <div className="property-modal-body">
          {/* Image Gallery Section */}
          <div className="property-modal-gallery">
            {property.images && property.images.length > 0 ? (
              <>
                <div className="main-image-container">
                  <img 
                    src={`http://localhost:5000${property.images[currentImageIndex]}`}
                    alt={`${property.title} - Image ${currentImageIndex + 1}`}
                    className="main-image"
                  />
                  
                  {property.images.length > 1 && (
                    <>
                      <button className="image-nav prev" onClick={prevImage}>
                        <i className="fas fa-chevron-left"></i>
                      </button>
                      <button className="image-nav next" onClick={nextImage}>
                        <i className="fas fa-chevron-right"></i>
                      </button>
                      
                      <div className="image-counter">
                        {currentImageIndex + 1} / {property.images.length}
                      </div>
                    </>
                  )}
                </div>
                
                {property.images.length > 1 && (
                  <div className="thumbnail-gallery">
                    {property.images.map((image, index) => (
                      <img
                        key={index}
                        src={`http://localhost:5000${image}`}
                        alt={`${property.title} - Thumbnail ${index + 1}`}
                        className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                        onClick={() => goToImage(index)}
                      />
                    ))}
                  </div>
                )}
              </>
            ) : (
              <div className="no-images">
                <i className="fas fa-image"></i>
                <p>No images available</p>
              </div>
            )}
          </div>

          {/* Property Details Section */}
          <div className="property-modal-details">
            <div className="property-header">
              <h2 className="property-title">{property.title}</h2>
              <div className="property-price">{property.price}</div>
            </div>

            <div className="property-location">
              <i className="fas fa-map-marker-alt"></i>
              <span>{property.location}</span>
            </div>

            <div className="property-area-badge">
              <i className="fas fa-building"></i>
              <span>{property.areaName}</span>
            </div>

            <div className="property-features-grid">
              <div className="feature-item">
                <i className="fas fa-bed"></i>
                <span>{property.beds} Bedrooms</span>
              </div>
              <div className="feature-item">
                <i className="fas fa-bath"></i>
                <span>{property.baths} Bathrooms</span>
              </div>
              <div className="feature-item">
                <i className="fas fa-expand-arrows-alt"></i>
                <span>{property.area}</span>
              </div>
            </div>

            {property.description && (
              <div className="property-description">
                <h3>Description</h3>
                <p>{property.description}</p>
              </div>
            )}

            {property.features && property.features.length > 0 && (
              <div className="property-amenities">
                <h3>Features & Amenities</h3>
                <div className="amenities-grid">
                  {property.features.map((feature, index) => (
                    <div key={index} className="amenity-item">
                      <i className="fas fa-check"></i>
                      <span>{feature}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* External Links */}
            <div className="property-links">
              <h3>View on Property Portals</h3>
              <div className="links-grid">
                {property.links?.acres99 && (
                  <a 
                    href={property.links.acres99} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="portal-link acres99"
                  >
                    <i className="fas fa-external-link-alt"></i>
                    View on 99acres
                  </a>
                )}
                {property.links?.magicbricks && (
                  <a 
                    href={property.links.magicbricks} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="portal-link magicbricks"
                  >
                    <i className="fas fa-external-link-alt"></i>
                    View on MagicBricks
                  </a>
                )}
              </div>
            </div>

            {/* Contact Section */}
            <div className="property-contact">
              <h3>Interested in this property?</h3>
              <p>Contact us for more details, site visits, and personalized assistance.</p>
              <div className="contact-buttons">
                <button 
                  className="btn btn-primary contact-btn"
                  onClick={() => {
                    onClose();
                    window.location.href = '/contact';
                  }}
                >
                  <i className="fas fa-phone"></i>
                  Contact Us
                </button>
                <a 
                  href="tel:+919811186086" 
                  className="btn btn-secondary call-btn"
                >
                  <i className="fas fa-phone-alt"></i>
                  Call Now
                </a>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PropertyDetailModal;
