import React, { useEffect, useLayoutEffect, useRef } from 'react';
import './Modal.css';

const Modal = ({ isOpen, onClose, subArea, onViewProperties }) => {
  const scrollPositionRef = useRef(0);
  const bodyRef = useRef(null);

  // Use useLayoutEffect to prevent scroll jumping
  useLayoutEffect(() => {
    if (isOpen) {
      // Store current scroll position immediately
      scrollPositionRef.current = window.pageYOffset || document.documentElement.scrollTop;
      
      // Store original body styles
      bodyRef.current = {
        position: document.body.style.position,
        top: document.body.style.top,
        width: document.body.style.width,
        overflow: document.body.style.overflow
      };
      
      // Prevent body scroll immediately
      document.body.style.position = 'fixed';
      document.body.style.top = `-${scrollPositionRef.current}px`;
      document.body.style.width = '100%';
      document.body.style.overflow = 'hidden';
      document.body.classList.add('modal-open');
      
      // Hide navbar immediately
      const navbar = document.querySelector('.nav-container');
      const mobileHeader = document.querySelector('.mobile-header');
      
      if (navbar) {
        navbar.style.display = 'none';
      }
      if (mobileHeader) {
        mobileHeader.style.display = 'none';
      }
    } else if (bodyRef.current) {
      // Restore body styles
      document.body.style.position = bodyRef.current.position;
      document.body.style.top = bodyRef.current.top;
      document.body.style.width = bodyRef.current.width;
      document.body.style.overflow = bodyRef.current.overflow;
      document.body.classList.remove('modal-open');
      
      // Restore scroll position without causing jump
      window.scrollTo(0, scrollPositionRef.current);
      
      // Show navbar
      const navbar = document.querySelector('.nav-container');
      const mobileHeader = document.querySelector('.mobile-header');
      
      if (navbar) {
        navbar.style.display = 'block';
      }
      if (mobileHeader) {
        mobileHeader.style.display = 'flex';
      }
    }
  }, [isOpen]);

  useEffect(() => {
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleEscape);
    }

    return () => {
      document.removeEventListener('keydown', handleEscape);
      
      // Cleanup on unmount
      if (bodyRef.current) {
        document.body.style.position = bodyRef.current.position;
        document.body.style.top = bodyRef.current.top;
        document.body.style.width = bodyRef.current.width;
        document.body.style.overflow = bodyRef.current.overflow;
        document.body.classList.remove('modal-open');
        
        // Restore scroll position
        window.scrollTo(0, scrollPositionRef.current);
        
        // Show navbar
        const navbar = document.querySelector('.nav-container');
        const mobileHeader = document.querySelector('.mobile-header');
        
        if (navbar) {
          navbar.style.display = 'block';
        }
        if (mobileHeader) {
          mobileHeader.style.display = 'flex';
        }
      }
    };
  }, [isOpen, onClose]);

  // Don't render anything if not open or no subArea
  if (!isOpen || !subArea) return null;

  const handleBackdropClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleViewProperties = () => {
    onViewProperties(subArea.areaKey);
  };

  return (
    <div 
      className="modal-overlay modal-active" 
      onClick={handleBackdropClick}
      role="dialog"
      aria-modal="true"
      aria-labelledby="modal-title"
    >
      <div className="modal-content">
        <button 
          className="modal-close" 
          onClick={onClose}
          aria-label="Close modal"
        >
          <i className="fas fa-times"></i>
        </button>
        
        <div className="modal-left">
          <div className="modal-map">
            <img 
              src="/assets/map.webp" 
              alt="Area Map" 
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.parentElement.innerHTML = '<span>Map View</span>';
              }}
            />
          </div>
        </div>

        <div className="modal-right">
          <h2 id="modal-title">{subArea.title}</h2>
          <div className="modal-description">
            <p className="modal-area-name">{subArea.parentArea.name}</p>
            <p className="modal-subarea-desc">{subArea.description}</p>
            <div className="modal-area-info">
              <h4>About {subArea.parentArea.name}</h4>
              <p>{subArea.parentArea.description}</p>
            </div>
          </div>
          <div className="modal-button">
            <button className="btn btn-primary" onClick={handleViewProperties}>
              View Properties in {subArea.parentArea.name}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
