import React, { useEffect, useRef } from 'react';
import './Modal.css';

const Modal = ({ isOpen, onClose, subArea, onViewProperties }) => {
  const scrollY = useRef(0);

  useEffect(() => {
    console.log('üîÑ Modal useEffect triggered - isOpen:', isOpen);
    
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      console.log('üìÇ Modal OPENING...');
      
      // Store current scroll position
      scrollY.current = window.scrollY;
      console.log('üìç Current scroll position stored:', scrollY.current);
      
      document.addEventListener('keydown', handleEscape);
      
      // Log scroll position before any changes
      console.log('üìè Scroll position before changes:', window.scrollY);
      
      // Prevent scrolling without changing position
      document.documentElement.style.scrollBehavior = 'auto';
      console.log('üö´ Smooth scrolling disabled');
      
      document.body.style.position = 'fixed';
      document.body.style.top = `-${scrollY.current}px`;
      document.body.style.width = '100%';
      document.body.classList.add('modal-open');
      
      console.log('üîí Body styles applied:', {
        position: document.body.style.position,
        top: document.body.style.top,
        width: document.body.style.width
      });
      
      // Log scroll position after body changes
      console.log('üìè Scroll position after body changes:', window.scrollY);
      
      // Hide both desktop navbar and mobile header
      const navbar = document.querySelector('.nav-container');
      const mobileHeader = document.querySelector('.mobile-header');
      
      if (navbar) {
        navbar.style.display = 'none';
        console.log('üôà Desktop navbar hidden');
      }
      if (mobileHeader) {
        mobileHeader.style.display = 'none';
        console.log('üôà Mobile header hidden');
      }
      
      console.log('‚úÖ Modal OPENED successfully');
      
    } else {
      console.log('üìÇ Modal CLOSING...');
      
      // Log scroll position before restoration
      console.log('üìè Scroll position before restoration:', window.scrollY);
      console.log('üìç Stored scroll position to restore:', scrollY.current);
      
      // Restore body styles
      document.body.style.position = '';
      document.body.style.top = '';
      document.body.style.width = '';
      document.body.classList.remove('modal-open');
      
      console.log('üîì Body styles restored');
      console.log('üìè Scroll position after body restoration:', window.scrollY);
      
      // Restore scroll position immediately
      window.scrollTo(0, scrollY.current);
      console.log('üîÑ Scroll position restored to:', scrollY.current);
      console.log('üìè Actual scroll position after restoration:', window.scrollY);
      
      // Restore smooth scrolling
      setTimeout(() => {
        document.documentElement.style.scrollBehavior = 'smooth';
        console.log('‚ú® Smooth scrolling re-enabled');
      }, 0);
      
      // Show both desktop navbar and mobile header
      const navbar = document.querySelector('.nav-container');
      const mobileHeader = document.querySelector('.mobile-header');
      
      if (navbar) {
        navbar.style.display = 'block';
        console.log('üëÅÔ∏è Desktop navbar shown');
      }
      if (mobileHeader) {
        mobileHeader.style.display = 'flex';
        console.log('üëÅÔ∏è Mobile header shown');
      }
      
      console.log('‚úÖ Modal CLOSED successfully');
    }

    return () => {
      console.log('üßπ Modal cleanup function called');
      document.removeEventListener('keydown', handleEscape);
      
      // Cleanup - restore body styles
      document.body.style.position = '';
      document.body.style.top = '';
      document.body.style.width = '';
      document.body.classList.remove('modal-open');
      document.documentElement.style.scrollBehavior = 'smooth';
      
      // Ensure both navbar and mobile header are shown when component unmounts
      const navbar = document.querySelector('.nav-container');
      const mobileHeader = document.querySelector('.mobile-header');
      
      if (navbar) {
        navbar.style.display = 'block';
      }
      if (mobileHeader) {
        mobileHeader.style.display = 'flex';
      }
      
      console.log('üßπ Modal cleanup completed');
    };
  }, [isOpen, onClose]);

  if (!isOpen || !subArea) return null;

  const handleBackdropClick = (e) => {
    console.log('üñ±Ô∏è Backdrop clicked');
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleViewProperties = () => {
    console.log('üè† View Properties button clicked');
    onViewProperties(subArea.areaKey);
  };

  console.log('üé≠ Modal render - isOpen:', isOpen, 'subArea:', subArea?.title);

  return (
    <div 
      className="modal-overlay" 
      onClick={handleBackdropClick}
      style={{ 
        zIndex: 2147483647,
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100vh',
        background: 'rgba(0, 0, 0, 0.8)'
      }}
    >
      <div className="modal-content" style={{ zIndex: 2147483647 }}>
        <button 
          className="modal-close" 
          onClick={onClose}
          style={{ zIndex: 2147483647 }}
        >
          <i className="fas fa-times"></i>
        </button>
        
        <div className="modal-left">
          <div className="modal-map">
            <img src="/assets/map.webp" alt="Area Map" style={{width: '100%', height: '100%', objectFit: 'cover'}} />
          </div>
        </div>

        <div className="modal-right">
          <h2>{subArea.title}</h2>
          <div className="modal-description">
            <p className="modal-area-name">{subArea.parentArea.name}</p>
            <p className="modal-subarea-desc">{subArea.description}</p>
            <div className="modal-area-info">
              <h4>About {subArea.parentArea.name}</h4>
              <p>{subArea.parentArea.description}</p>
            </div>
          </div>
          <div className="modal-button">
            <button className="btn btn-primary" onClick={handleViewProperties}>
              View Properties in {subArea.parentArea.name}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
