import React, { useEffect, useRef, useState } from "react";
import "./ImageSlider.css";

const images = [
  {
    url: "https://images.unsplash.com/photo-1582407947304-fd86f028f716?q=80&w=1296&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#luxury-apartments",
    alt: "Luxury Apartment"
  },
  {
    url: "https://images.unsplash.com/photo-1560448204-e02f11c3d0e2?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#modern-cafe",
    alt: "Modern Cafe"
  },
  {
    url: "https://images.unsplash.com/photo-1560185127-6ed189bf02f4?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#cozy-living",
    alt: "Cozy Living"
  },
  {
    url: "https://images.unsplash.com/photo-1560184897-ae75f418493e?q=80&w=1170&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#urban-style",
    alt: "Urban Style"
  },
  {
    url: "https://images.unsplash.com/photo-1505873242700-f289a29e1e0f?q=80&w=1176&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D",
    link: "#premium-office",
    alt: "Premium Office"
  }
];

const VISIBLE_COUNT = 5;

const ImageSlider = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);
  const timeoutRef = useRef(null);
  const delay = 3000;

  useEffect(() => {
    const startAutoSlide = () => {
      timeoutRef.current = setTimeout(() => {
        nextSlide();
      }, delay);
    };

    startAutoSlide();

    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, [currentIndex]);

  const nextSlide = () => {
    if (isTransitioning) return;
    setIsTransitioning(true);
    setCurrentIndex((prev) => (prev + 1) % images.length);
    
    setTimeout(() => {
      setIsTransitioning(false);
    }, 800);
  };

  const prevSlide = () => {
    if (isTransitioning) return;
    setIsTransitioning(true);
    setCurrentIndex((prev) => (prev - 1 + images.length) % images.length);
    
    setTimeout(() => {
      setIsTransitioning(false);
    }, 800);
  };

  // Create extended array for seamless loop
  const getExtendedImages = () => {
    const extended = [];
    
    // Add last few images at the beginning for seamless transition
    for (let i = 0; i < VISIBLE_COUNT; i++) {
      extended.push(images[(images.length - VISIBLE_COUNT + i + images.length) % images.length]);
    }
    
    // Add all original images
    extended.push(...images);
    
    // Add first few images at the end for seamless transition
    for (let i = 0; i < VISIBLE_COUNT; i++) {
      extended.push(images[i % images.length]);
    }
    
    return extended;
  };

  const extendedImages = getExtendedImages();
  const translateX = -(currentIndex + VISIBLE_COUNT) * (100 / VISIBLE_COUNT);

  return (
    <div className="carousel-slider-bg">
      <div className="carousel-slider">
        <button 
          className="carousel-arrow left" 
          onClick={prevSlide}
          disabled={isTransitioning}
          aria-label="Previous images"
        >
          &#8592;
        </button>
        
        <div className="carousel-track-container">
          <div 
            className="carousel-track"
            style={{
              transform: `translateX(${translateX}%)`,
              transition: isTransitioning ? 'transform 0.8s cubic-bezier(0.25, 0.46, 0.45, 0.94)' : 'none',
              width: `${(extendedImages.length * 100) / VISIBLE_COUNT}%`
            }}
          >
            {extendedImages.map((img, idx) => (
              <div 
                key={`${img.alt}-${idx}`} 
                className="carousel-img-container"
                style={{ width: `${100 / extendedImages.length}%` }}
              >
                <a href={img.link} className="carousel-img-link">
                  <img src={img.url} alt={img.alt} className="carousel-img" />
                </a>
              </div>
            ))}
          </div>
        </div>

        <button 
          className="carousel-arrow right" 
          onClick={nextSlide}
          disabled={isTransitioning}
          aria-label="Next images"
        >
          &#8594;
        </button>
      </div>
      
      {/* Optional: Dots indicator */}
      <div className="carousel-dots">
        {images.map((_, idx) => (
          <button
            key={idx}
            className={`carousel-dot ${idx === currentIndex ? 'active' : ''}`}
            onClick={() => {
              if (!isTransitioning) {
                setIsTransitioning(true);
                setCurrentIndex(idx);
                setTimeout(() => setIsTransitioning(false), 800);
              }
            }}
            aria-label={`Go to slide ${idx + 1}`}
          />
        ))}
      </div>
    </div>
  );
};

export default ImageSlider;
