import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchAreas } from '../services/api';
import PageTransition from './PageTransition';
import Modal from './SocietyModal'; // New modal for societies
import './Societies.css';

const Societies = () => {
  const { areaKey, subAreaId } = useParams();
  const navigate = useNavigate();
  const [subArea, setSubArea] = useState(null);
  const [societies, setSocieties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedSociety, setSelectedSociety] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [cardPosition, setCardPosition] = useState(null);

  useEffect(() => {
    loadSubAreaData();
  }, [areaKey, subAreaId]);

  const loadSubAreaData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      // Fetch areas data
      const areasData = await fetchAreas();
      
      if (areasData[areaKey]) {
        const area = areasData[areaKey];
        const foundSubArea = area.subAreas?.find(sa => sa.id === parseInt(subAreaId));
        
        if (foundSubArea) {
          setSubArea({
            ...foundSubArea,
            parentArea: area,
            areaKey: areaKey
          });
          
          // Set societies from sub-area data (societies are stored in subArea.societies)
          setSocieties(foundSubArea.societies || []);
        } else {
          setError('Sub-area not found');
        }
      } else {
        setError('Area not found');
      }
    } catch (error) {
      console.error('Error loading sub-area data:', error);
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleSocietyClick = (society, event) => {
    console.log('🏘️ Society clicked:', society);
    
    // Get the clicked card element
    const cardElement = event.currentTarget;
    const rect = cardElement.getBoundingClientRect();
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    
    // Calculate the absolute position of the card
    const cardAbsolutePosition = {
      y: rect.top + scrollTop - 100,
      x: rect.left,
      element: cardElement
    };
    
    setCardPosition(cardAbsolutePosition);
    setSelectedSociety(society);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setTimeout(() => {
      setSelectedSociety(null);
      setTimeout(() => {
        setCardPosition(null);
      }, 1000);
    }, 300);
  };

  const handleBackClick = () => {
    navigate(-1);
  };

  // Function to get society image
  const getSocietyImage = (society) => {
    if (society.mapImage) {
      return `http://localhost:5000${society.mapImage}`;
    } else {
      return '/assets/map.webp';
    }
  };

  if (loading) {
    return (
      <PageTransition>
        <div className="societies-page">
          <div className="container">
            <div className="loading-spinner" style={{ margin: '100px auto' }}></div>
            <p style={{ textAlign: 'center' }}>Loading societies...</p>
          </div>
        </div>
      </PageTransition>
    );
  }

  if (error || !subArea) {
    return (
      <PageTransition>
        <div className="societies-page">
          <div className="container">
            <div className="error-message">
              <h2>{error || 'Sub-area not found'}</h2>
              <p>The requested sub-area could not be found.</p>
              <button 
                className="btn btn-primary"
                onClick={handleBackClick}
              >
                Go Back
              </button>
            </div>
          </div>
        </div>
      </PageTransition>
    );
  }

  return (
    <PageTransition>
      <div className="societies-page">
        <div className="container">
          {/* Back Navigation */}
          <div className="back-navigation">
            <button 
              className="back-btn"
              onClick={handleBackClick}
            >
              <i className="fas fa-arrow-left"></i>
              Back
            </button>
          </div>

          {/* Page Header */}
          <div className="societies-header">
            <div className="breadcrumb">
              <span>{subArea.parentArea.name}</span>
              <i className="fas fa-chevron-right"></i>
              <span>{subArea.title}</span>
            </div>
            <h1>Societies in {subArea.title}</h1>
            <p className="societies-description">{subArea.description}</p>
          </div>

          {/* Societies Grid */}
          {societies.length > 0 ? (
            <div className="societies-grid">
              {societies.map((society) => (
                <div
                  key={society.id}
                  className="society-card"
                  onClick={(event) => handleSocietyClick(society, event)}
                >
                  <div className="society-image">
                    <img 
                      src={getSocietyImage(society)}
                      alt={`${society.name} Map`}
                      onError={(e) => {
                        if (society.mapImage && e.target.src !== '/assets/map.webp') {
                          e.target.src = '/assets/map.webp';
                        }
                      }}
                    />
                  </div>
                  <div className="society-content">
                    <h3>{society.name}</h3>
                    <p className="society-type">{society.type || 'Residential Society'}</p>
                    <p className="society-description">{society.description}</p>
                    
                    {/* Society Features */}
                    {society.features && society.features.length > 0 && (
                      <div className="society-features">
                        {society.features.slice(0, 3).map((feature, index) => (
                          <span key={index} className="feature-tag">
                            {feature}
                          </span>
                        ))}
                        {society.features.length > 3 && (
                          <span className="feature-tag more">
                            +{society.features.length - 3} more
                          </span>
                        )}
                      </div>
                    )}
                  </div>
                  
                  {/* Hover overlay */}
                  <div className="society-overlay">
                    <i className="fas fa-eye"></i>
                    <span>View Details</span>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-societies">
              <div className="no-societies-content">
                <i className="fas fa-building"></i>
                <h3>No Societies Found</h3>
                <p>No societies have been added to {subArea.title} yet.</p>
                <button 
                  className="btn btn-primary"
                  onClick={handleBackClick}
                >
                  Go Back
                </button>
              </div>
            </div>
          )}
        </div>

        {/* Society Modal */}
        <Modal
          isOpen={isModalOpen}
          onClose={handleCloseModal}
          society={selectedSociety}
          subArea={subArea}
          cardPosition={cardPosition}
        />
      </div>
    </PageTransition>
  );
};

export default Societies;
