import React, { useEffect } from 'react';
import './Modal.css';

const Modal = ({ isOpen, onClose, subArea, onViewProperties }) => {
  useEffect(() => {
    console.log('üîÑ Modal useEffect triggered - isOpen:', isOpen);
    
    const handleEscape = (e) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    if (isOpen) {
      console.log('üìÇ Modal OPENING...');
      console.log('üìè Scroll position BEFORE any modal changes:', window.scrollY);
      
      document.addEventListener('keydown', handleEscape);
      
      // Log before applying overflow hidden
      console.log('üìè Scroll position BEFORE overflow hidden:', window.scrollY);
      
      document.body.style.overflow = 'hidden';
      console.log('üö´ Body overflow set to hidden');
      
      // Check scroll position immediately after overflow hidden
      console.log('üìè Scroll position AFTER overflow hidden:', window.scrollY);
      
      document.body.classList.add('modal-open');
      console.log('üè∑Ô∏è modal-open class added to body');
      
      // Check scroll position after class added
      console.log('üìè Scroll position AFTER modal-open class:', window.scrollY);
      
      // Hide desktop navbar
      const navbar = document.querySelector('.nav-container');
      if (navbar) {
        navbar.style.display = 'none';
        console.log('üôà Desktop navbar hidden');
      }
      
      // Hide mobile header and sidebar
      const mobileHeader = document.querySelector('.mobile-header');
      if (mobileHeader) {
        mobileHeader.style.display = 'none';
        console.log('üôà Mobile header hidden');
      }
      
      const mobileSidebar = document.querySelector('.mobile-sidebar');
      if (mobileSidebar) {
        mobileSidebar.style.display = 'none';
        console.log('üôà Mobile sidebar hidden');
      }
      
      // Final scroll position check
      console.log('üìè FINAL scroll position after modal opened:', window.scrollY);
      console.log('‚úÖ Modal OPENED successfully');
      
    } else {
      console.log('üìÇ Modal CLOSING...');
      console.log('üìè Scroll position BEFORE modal close:', window.scrollY);
      
      document.body.style.overflow = 'unset';
      console.log('‚úÖ Body overflow restored to unset');
      
      // Check scroll position after overflow restored
      console.log('üìè Scroll position AFTER overflow restored:', window.scrollY);
      
      document.body.classList.remove('modal-open');
      console.log('üè∑Ô∏è modal-open class removed from body');
      
      // Check scroll position after class removed
      console.log('üìè Scroll position AFTER class removed:', window.scrollY);
      
      // Show desktop navbar
      const navbar = document.querySelector('.nav-container');
      if (navbar) {
        navbar.style.display = 'block';
        console.log('üëÅÔ∏è Desktop navbar shown');
      }
      
      // Show mobile header and sidebar
      const mobileHeader = document.querySelector('.mobile-header');
      if (mobileHeader) {
        mobileHeader.style.display = '';
        console.log('üëÅÔ∏è Mobile header shown');
      }
      
      const mobileSidebar = document.querySelector('.mobile-sidebar');
      if (mobileSidebar) {
        mobileSidebar.style.display = '';
        console.log('üëÅÔ∏è Mobile sidebar shown');
      }
      
      // Final scroll position check
      console.log('üìè FINAL scroll position after modal closed:', window.scrollY);
      console.log('‚úÖ Modal CLOSED successfully');
    }

    return () => {
      console.log('üßπ Modal cleanup function called');
      console.log('üìè Scroll position BEFORE cleanup:', window.scrollY);
      
      document.removeEventListener('keydown', handleEscape);
      document.body.style.overflow = 'unset';
      document.body.classList.remove('modal-open');
      
      // Show desktop navbar
      const navbar = document.querySelector('.nav-container');
      if (navbar) navbar.style.display = 'block';
      
      // Show mobile header and sidebar
      const mobileHeader = document.querySelector('.mobile-header');
      if (mobileHeader) mobileHeader.style.display = '';
      
      const mobileSidebar = document.querySelector('.mobile-sidebar');
      if (mobileSidebar) mobileSidebar.style.display = '';
      
      console.log('üìè Scroll position AFTER cleanup:', window.scrollY);
      console.log('üßπ Modal cleanup completed');
    };
  }, [isOpen, onClose]);

  if (!isOpen || !subArea) return null;

  const handleBackdropClick = (e) => {
    console.log('üñ±Ô∏è Backdrop clicked');
    console.log('üìè Scroll position at backdrop click:', window.scrollY);
    
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleViewProperties = () => {
    console.log('üè† View Properties button clicked');
    console.log('üìè Scroll position at button click:', window.scrollY);
    
    onViewProperties(subArea.areaKey);
  };

  console.log('üé≠ Modal render - isOpen:', isOpen, 'subArea:', subArea?.title);

  return (
    <div 
      className="modal-overlay"
      onClick={handleBackdropClick}
    >
      <div className="modal-content">
        <button 
          className="modal-close" 
          onClick={onClose}
        >
          <i className="fas fa-times"></i>
        </button>
        
        <div className="modal-left">
          <div className="modal-map">
            <img src="/assets/map.webp" alt="Area Map" style={{width: '100%', height: '100%', objectFit: 'cover'}} />
          </div>
        </div>

        <div className="modal-right">
          <h2>{subArea.title}</h2>
          <div className="modal-description">
            <p className="modal-area-name">{subArea.parentArea.name}</p>
            <p className="modal-subarea-desc">{subArea.description}</p>
            <div className="modal-area-info">
              <h4>About {subArea.parentArea.name}</h4>
              <p>{subArea.parentArea.description}</p>
            </div>
          </div>
          <div className="modal-button">
            <button className="btn btn-primary" onClick={handleViewProperties}>
              View Properties in {subArea.parentArea.name}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
